mapper.select:
select *  from sign


        and name like concat('%',#{name},'%')


        and number=#{number}


        and sign_time=#{signTime}

select count(id) from sign


     and sign_time > #{begin}


     and sign_time < #{end}


     and sign_status = #{signStatus}

mapper.update:
update sign
name = #{name}, sign_status = #{signStatus}, sign_time = #{signTime}, reason = #{reason}, specific_reason = #{specificReason}, dormitory_number = #{dormitoryNumber},
where id = #{id}
