mapper.update:
update student
name = #{name}, number = #{number}, sex = #{sex}, age = #{age}, phone = #{phone}, dormitory_id = #{dormitoryId}, dormitory_number = #{dormitoryNumber}, building_number = #{buildingNumber}, finger_print = #{fingerPrint}, sign_status = #{signStatus},
where id = #{id}

mapper.select:
select *  from student


        and name like concat('%',#{name},'%')


        and dormitory_number=#{dormitoryNumber}


        and number=#{number}
