mapper.select:
select *  from sign


        and d.name like concat('%',#{name},'%')


        and number=#{number}

mapper.update:
update sign
name = #{name}, sign_status = #{signStatus}, sign_time = #{signTime}, dormitory_number = #{dormitoryNumber},
where number = #{number}

mapper.select:
select sum(sign_status) from sign


        and sign_time > #{beginTime}


        and sign_time < #{endTime}
