mapper.update:
update student
name = #{name}, sex = #{sex}, age = #{age}, phone = #{phone}, dormitory_number = #{dormitory_number}, building_number = #{building_number}, finger_print = #{finger_print}, sign_status = #{sign_status},
where number = #{number}

mapper.select:
select *  from student


        and d.name like concat('%',#{name},'%')


        and number=#{number}
