mapper.update:
update student
name = #{name}, sex = #{sex}, age = #{age}, phone = #{phone}, dormitory_number = #{dormitoryNumber}, building_number = #{buildingNumber}, finger_print = #{fingerPrint}, sign_status = #{signStatus},
where number = #{number}

mapper.select:
select *  from student


        and d.name like concat('%',#{name},'%')


        and number=#{number}
